mapper.select:
select exists(
               select id
               from public.examinations
               where id = #{id}
                 and user_id = #{userId}
                 and finished_at > #{finishedAtAfter}
           )

mapper.update:
update public.examinations
set finished_at = #{finishedAt}
where id = #{id}

mapper.select:
select *
from public.examinations e
where e.user_id = #{userId}

  and e.finished_at < #{finishedAt}

order by e.id

select * from public.examinations e where
e.finished_at < #{finishedAt}
order by e.id

mapper.delete:
delete
from public.examinations e
where e.id = #{id}
