mapper.update:
update public.subjects
set title             = #{entity.title},
    specialization_id = #{entity.specializationId}
where id = #{id}

mapper.select:
select data.subjectId as subjectId, data.subjectTitle as subjectTitle, data.specializationId as specializationId, data.specializationTitle as specializationTitle, data.stepId as stepId, data.stepTitle as stepTitle, passed.importSubjectTitle as importSubjectTitle, passed.importSpecializationTitle as importSpecializationTitle, passed.importStepTitle as importStepTitle from (select unnest(array [
#{title}
]::text[]) as importSubjectTitle, unnest(array [
#{title}
]::text[]) as importSpecializationTitle, unnest(array [
#{title}
]::text[]) as importStepTitle ) passed left join lateral ( select sub.id as subjectId, sub.title as subjectTitle, spec.id as specializationId, spec.title as specializationTitle, s.id as stepId, s.title as stepTitle from public.subjects sub left join public.specializations spec on spec.id = sub.specialization_id left join public.steps s on s.id = spec.step_id where sub.title ilike importSubjectTitle and (importSpecializationTitle is null or spec.title ilike importSpecializationTitle) and (importStepTitle is null or s.title ilike importStepTitle) ) data on true
